import { SECTIONS } from "./data.js";
import { useState } from "react";


function NewPostForm() {
  const [title, setText] = useState("");
  const [descriptionText, setDescriptionText] = useState("");
  const [catagory, setCatagory] = useState("");

  //This is for the textarea
  function autoGrow(event) {
    event.target.style.height = "5px";
    event.target.style.height = event.target.scrollHeight + "px";
  }

  function handleSumbit(event) {
    event.preventDefault();

    if (title && descriptionText && catagory) {
      const newPost = {
        id:1,
        title: "Ronaldo is the best soccer player in history",
        created_at: new Date(),
        description: "Cristiano Ronaldo is widely regarded as one of the greatest soccer players of his generation. His goal-scoring ability, athleticism, and versatility have earned him numerous awards and accolades throughout his career. ",
        category: "Soccer",
        thumbsUp: 0,
        thumbsDown: 0
      }
    }
  }
  return (
    <form className="post-form" onSubmit={() => handleSumbit}>
      {/* <input type="text" placeholder="Hot Take"/> */}
      {/* Read value */}
      <textarea
        class="form-description"
        placeholder="Hot Take"
        value={text}
        style={{ resize: "none" }}
        onInput={autoGrow}
        onChange={(eventObj) => setText(eventObj.target.value)}
      ></textarea>
      <textarea
        class="form-description"
        placeholder="Explain Your Hot Take"
        value={descriptionText}
        style={{ resize: "none" }}
        onInput={autoGrow}
        onChange={(eventObj) => setDescriptionText(eventObj.target.value)}
      ></textarea>
      <span>{descriptionText.split(" ").length - 1}</span>
      <select
        value={catagory}
        onChange={(eventObj) => setCatagory(eventObj.target.value)}
      >
        <option value="">Choose Topic</option>
        {SECTIONS.map((section) => (
          <option key={section.name} value={section.name}>
            {section.name}
          </option>
        ))}
      </select>
      <button class="btn">Post</button>
    </form>
  );
}
export default NewPostForm;
