}
    return ( <form className="post-form" onSubmit={() => handleSumbit}> 
    {/* <input type="text" placeholder="Hot Take"/> */}
    {/* Read value */}
    <textarea class="form-description" placeholder="Hot Take" value={text} style= {{resize: "none"}} onInput={autoGrow} onChange={(eventObj) => setText(eventObj.target.value)}></textarea>
    <textarea class="form-description" placeholder="Explain Your Hot Take" value={descriptionText} style= {{resize: "none"}} onInput={autoGrow} onChange={(eventObj) => setDescriptionText(eventObj.target.value)}></textarea>
    <span>{descriptionText.split(" ").length - 1}</span>
    <select  value={catagory} onChange={(eventObj) => setCatagory(eventObj.target.value)}>
        <option value="">Choose Topic</option>
       {SECTIONS.map((section) => <option key={section.name} value={section.name}>{section.name}</option>)}
    </select>
    <button class="btn">Post</button></form>);
  }
  export default NewPostForm;
