                if(section){
                    backgroundColor = section.color;
                }
                if(["Soccer","F1","Home"].includes(section.name)){
                    textColor = "#000000";
                }
  return <aside className="sidebar">
    <ul>
      {SECTIONS.map((section) => (
        <li><button className="btn"
          style={{ backgroundColor: section.color, color: "#000000", marginBottom: "20px" }}>{section.name}</button>
        </li>
        ))}
    </ul></aside>;
}

