import {useState} from "react";

function show() {
    const [count, setCount] = useState(0);


    return (
        <div>
            <span style={{fontSize: "40px"}}>{count}</span>
            <button className="btn" onClick={() => setCount((count) => count + 1)}>+1</button>

        </div>
    );
}

export default Counter;
